import React, {useContext, Fragment} from "react";
import { NavLink } from "react-router-dom";
import {  CurrentUserContext  } from '../contexts/currentUser'


const Topbar = () => {
  const [currentUserState] = useContext(CurrentUserContext)

  const userImage = (currentUserState.isLoggedIn && currentUserState.currentUser.image) || 
  'https://static.productionready.io/images/smiley-cyrus.jpg'
  

  return (
    <nav className="navbar navbar-light">
      <div className="container">
        <ul className="topbar">
         <li className="topbar-list">
                <NavLink activeStyle={{ background: "#000" }} to="/" exact>
                  Home
                </NavLink>
        </li>
        {currentUserState.isLoggedIn && (
         <Fragment>
            <li className="topbar-list">
                <NavLink activeStyle={{ background: "#000" }} to="/articles/new" exact>
                  New Post
                </NavLink>
              </li>
            <li className="topbar-list">
                <NavLink activeStyle={{ background: "#000" }} to="/settings" exact>
                  Settings
                </NavLink>
              </li>
            <li className="topbar-list">
                <NavLink activeStyle={{ background: "#000" }} to={`/profiles/${currentUserState.currentUser.username}`}  exact>
                  <img className="user-img" src={userImage} alt="user_photo" />
                  &nbsp;
                  <span className="user-text-img"> {currentUserState.currentUser.username}</span>
                </NavLink>
              </li>
         </Fragment>
        )}
        {currentUserState.isLoggedIn === false && (
          <Fragment>
             
              <li>
                <NavLink activeStyle={{ background: "#000" }} to="/login" exact>
                  Sign In
                </NavLink>
              </li>
              <li>
                <NavLink activeStyle={{ background: "#000" }} to="/register" exact>
                  Sign Up
                </NavLink>
            </li>
          </Fragment>
        )}
         
        </ul>
      </div>
    </nav>
  );
};
export default Topbar;
